import { useCallback, useEffect, useMemo, useState } from "react";
import axios from "axios";

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://127.0.0.1:8000";
const TOLA_CONVERSION = 0.375;

const round2 = (value) =>
  typeof value === "number" && Number.isFinite(value)
    ? value.toFixed(2)
    : "-";

const formatPercent = (value) => {
  if (value === null || value === undefined || Number.isNaN(value)) {
    return "-";
  }
  const val = Number(value).toFixed(2);
  return Number(value) > 0 ? `+${val}%` : `${val}%`;
};

function App() {
  const [latest, setLatest] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState("");

  const fetchLatest = useCallback(async () => {
    setLoading(true);
    setError("");
    try {
      const response = await axios.get(`${API_BASE_URL}/predict/latest`);
      setLatest(response.data);
    } catch (err) {
      const message = err.response?.data?.detail || err.message || "Unknown error";
      setError(message);
    } finally {
      setLoading(false);
    }
  }, []);

  useEffect(() => {
    fetchLatest();
  }, [fetchLatest]);

  const metrics = useMemo(() => {
    if (!latest) {
      return null;
    }

    const todayOz = Number(latest.today_price ?? NaN);
    const todayTola = todayOz * TOLA_CONVERSION;
    const tomorrowOz = Number(latest.predicted_tomorrow_price ?? NaN);
    const tomorrowTola = tomorrowOz * TOLA_CONVERSION;
    const change = Number(latest.predicted_change_percent ?? NaN);

    return {
      date: latest.date ? new Date(latest.date).toLocaleDateString() : "-",
      todayOz: round2(todayOz),
      todayTola: round2(todayTola),
      tomorrowOz: round2(tomorrowOz),
      tomorrowTola: round2(tomorrowTola),
      change,
      changeText: formatPercent(change),
      changeClass:
        Number.isFinite(change) && change > 0
          ? "positive"
          : Number.isFinite(change) && change < 0
          ? "negative"
          : "",
    };
  }, [latest]);

  return (
    <div className="page">
      <div className="container">
        <header className="header">
          <span className="icon" role="img" aria-label="gold">
            ðŸ’°
          </span>
          <h1>Gold Price Predictor</h1>
          <p className="subhead">
            Date: {metrics?.date ?? "-"}
          </p>
        </header>

        <div className="actions">
          <button onClick={fetchLatest} disabled={loading}>
            {loading ? "Refreshing..." : "Refresh"}
          </button>
        </div>

        {error && <div className="error">{error}</div>}

        <main className="cards">
          <section className="card">
            <h2>Today</h2>
            <ul>
              <li>
                <span className="label">Per Ounce</span>
                <span className="value">${metrics?.todayOz ?? "-"}</span>
              </li>
              <li>
                <span className="label">Per Tola</span>
                <span className="value">${metrics?.todayTola ?? "-"}</span>
              </li>
            </ul>
          </section>

          <section className="card">
            <h2>Tomorrow (Predicted)</h2>
            <ul>
              <li>
                <span className="label">Per Ounce</span>
                <span className="value">${metrics?.tomorrowOz ?? "-"}</span>
              </li>
              <li>
                <span className="label">Per Tola</span>
                <span className="value">${metrics?.tomorrowTola ?? "-"}</span>
              </li>
            </ul>
          </section>

          <section className="card change-card">
            <h2>Predicted Change</h2>
            <p className={`change ${metrics?.changeClass ?? ""}`}>
              {metrics?.changeText ?? "-"}
            </p>
          </section>
        </main>
      </div>
    </div>
  );
}

export default App;
