import { useCallback, useEffect, useMemo, useState } from "react";
import axios from "axios";
import {
    Line,
    LineChart,
    ResponsiveContainer,
    Tooltip,
    XAxis,
    YAxis,
    CartesianGrid,
} from "recharts";

const API_BASE_URL = process.env.REACT_APP_API_BASE_URL || "http://127.0.0.1:8000";

const formatNumber = (value, digits = 2) => {
    if (value === null || value === undefined || Number.isNaN(value)) {
        return "-";
    }
    return Number(value).toFixed(digits);
};

const formatChange = (value) => {
    if (value === null || value === undefined || Number.isNaN(value)) {
        return "-";
    }
    const formatted = Number(value).toFixed(3);
    return value > 0 ? `+${formatted}%` : `${formatted}%`;
};

function App() {
    const [prediction, setPrediction] = useState(null);
    const [history, setHistory] = useState([]);
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState("");

    const fetchData = useCallback(async () => {
        setLoading(true);
        setError("");
        try {
            const [latestResponse, historyResponse] = await Promise.all([
                axios.get(`${API_BASE_URL}/predict/latest`),
                axios.get(`${API_BASE_URL}/history`, { params: { limit: 30 } }),
            ]);

            setPrediction(latestResponse.data);
            setHistory(historyResponse.data?.data ?? []);
        } catch (err) {
            const message = err.response?.data?.detail || err.message || "Unknown error";
            setError(message);
        } finally {
            setLoading(false);
        }
    }, []);

    useEffect(() => {
        fetchData();
    }, [fetchData]);

    const stats = useMemo(() => {
        if (!prediction) {
            return null;
        }

        return {
            date: prediction.date,
            todayPrice: formatNumber(prediction.today_price, 2),
            changePct: formatChange(prediction.predicted_change_percent),
            tomorrowPrice: formatNumber(prediction.predicted_tomorrow_price, 2),
            changeClass:
                prediction.predicted_change_percent === null || prediction.predicted_change_percent === undefined
                    ? ""
                    : prediction.predicted_change_percent >= 0
                        ? "positive"
                        : "negative",
        };
    }, [prediction]);

    return (
        <div className="app">
            <header className="header">
                <h1>Gold Price Predictor</h1>
                <p>Offline machine learning pipeline predicting tomorrow&apos;s gold price.</p>
            </header>

            <section className="controls">
                <button onClick={fetchData} disabled={loading}>
                    {loading ? "Refreshing..." : "Refresh"}
                </button>
                {stats?.date && <span className="as-of">As of {stats.date}</span>}
            </section>

            {error && <div className="error">{error}</div>}

            <section className="grid">
                <div className="card">
                    <h2>Today&apos;s Close</h2>
                    <p className="value">${stats?.todayPrice ?? "-"}</p>
                </div>
                <div className="card">
                    <h2>Predicted Change</h2>
                    <p className={`value ${stats?.changeClass ?? ""}`}>
                        {stats?.changePct ?? "-"}
                    </p>
                </div>
                <div className="card">
                    <h2>Tomorrow&apos;s Forecast</h2>
                    <p className="value">${stats?.tomorrowPrice ?? "-"}</p>
                </div>
            </section>

            <section className="chart-card">
                <div className="chart-header">
                    <h2>Recent Price Trend</h2>
                    <span>Last {history.length} sessions</span>
                </div>
                <div className="chart-wrapper">
                    {history.length > 0 ? (
                        <ResponsiveContainer width="100%" height={300}>
                            <LineChart data={history} margin={{ top: 16, right: 24, bottom: 8, left: 0 }}>
                                <CartesianGrid strokeDasharray="3 3" stroke="#e0e0e0" />
                                <XAxis dataKey="date" tick={{ fontSize: 12 }} minTickGap={20} />
                                <YAxis domain={["auto", "auto"]} tick={{ fontSize: 12 }} />
                                <Tooltip
                                    formatter={(value) => [`$${formatNumber(value, 2)}`, "Price"]}
                                    labelFormatter={(label) => `Date: ${label}`}
                                />
                                <Line type="monotone" dataKey="price" stroke="#0d6efd" strokeWidth={2} dot={false} />
                            </LineChart>
                        </ResponsiveContainer>
                    ) : (
                        <div className="chart-empty">No historical data available.</div>
                    )}
                </div>
            </section>
        </div>
    );
}

export default App;
